import "./Header.css";
export default function Header(props){
    const{theme,setTheme}=props;
    function toggleTheme(){
        if(theme=="daymode"){
            setTheme("nightmode")
        }else{
            setTheme("daymode")
        }
    }
    return(
        <header>
            <div className="logo">
                <span>task management</span>
            </div>
            <div className="theme-container">
                <span>{theme==="daymode"?"day mode":"night mode"}</span>
                <span className="icon" onClick={toggleTheme}>switch</span>
            </div>
        </header>
    );
}
